(28483, 258, '_sss|sss_student_name|0|0|value', 'Nishu Rathor'),
(28484, 258, '_sss|sss_course_name|0|0|value', 'Advanced Computer Courser'),
(28485, 258, '_sss|sss_student_message|0|0|value', 'Sir explained to us in a very easy manner, making it extremely easy for us to learn about computers. He has excellent knowledge of computers and explained each topic with examples. In the end, I would like to express my gratitude to Sir, and I recommend everyone who wants to learn about computers to join here.\r\n<br>\r\nThank you.'),
(28486, 258, '_sss|sss_designation|0|0|value', 'Class X Student'),
